    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TAKES input from TextFields and GENERATES bill button 
        String ContactNumber
                = jTextField1.getText();     
        String CustomerName
                = jTextField2.getText();
        String DeliveryAddress
                = jTextField7.getText();
        int qty
                = Integer.parseInt(
                        jTextField3.getText());
        double rate = 0;
        String DisplayInihawChoice = null;
        String DisplayAddonChoice1 = " ";
        String DisplayAddonChoice2 = " ";
        String DisplayAddonChoice3 = " ";
        String DisplayAddonChoice4 = " ";
        
        //  *****************************
        //  Inihaw Price                *
        //  *****************************
        if (jRadioButton1.isSelected()) {
                rate = 360;
                DisplayInihawChoice = "Liempo - P360";
        }
        else if (jRadioButton2.isSelected()) {
                rate = 260;
                DisplayInihawChoice = "Lechon Manok - P260";
        }
        else if (jRadioButton3.isSelected()) {
                rate = 188;
                DisplayInihawChoice = "Bangus - P188";
        }
        // DISPLAYS rate of selected Inihaw
        // type in TextField
        jTextField4.setText(
                "" + rate);

        double topamt = 0;

        //  *****************************
        //  Add-ons Price               *
        //  *****************************
        if (jCheckBox1.isSelected()) {
                topamt = 60;
                DisplayAddonChoice1 = "*Burong Hipon* ";
        }
        if (jCheckBox2.isSelected()) {
                topamt = topamt + 30;
                DisplayAddonChoice2 = "*Lemon Garlic Sarsa* ";
        }
        if (jCheckBox3.isSelected()) {
                topamt = topamt + 40;
                DisplayAddonChoice3 = "*Burong Isda* ";
        }
        if (jCheckBox4.isSelected()) {
                topamt = topamt + 50;
                DisplayAddonChoice4 = "*Bagoong* ";
        }
        // DISPLAYS total amount of
        // selected add-ons in TextField
        jTextField6.setText(
                ""
                + topamt);

        // Total amount is calculated
        double totalpayable
                = (rate * qty) + topamt;
        jTextField5.setText("" + totalpayable);

        // DISPLAYS order details
        jTextArea1
                .setText(
                        "Contact: \t" + ContactNumber
                        + "\nCustomer: \t" + CustomerName
                        + "\nAddress: \t" + DeliveryAddress        
                        + "\nInihaw: \t" + DisplayInihawChoice
                        + "\nQuantity: \t" + qty        
                        + "\nAdd-ons: \t " + DisplayAddonChoice1 
                                + DisplayAddonChoice2 
                                + DisplayAddonChoice3 
                                + DisplayAddonChoice4  
                        + "\nAMOUNT PAYABLE IS: â‚±" + totalpayable);

        
    }//GEN-LAST:event_jButton1ActionPerformed

